            return {
                "resourceType": "Location",
                "identifier": null,
                "name": null,
                "description": null,
                "type": { "coding": [] },
                "telecom": [],
                "address": [],
                "physicalType": { "coding": [] },
                "managingOrganization": null,
                "partOf": null,
                "position": null,
                "mode": null,
                "status": null}
